// Import needed libraries
const log = require('logToConsole');
const JSON = require('JSON');
const callInWindow = require('callInWindow');
const setInWindow = require('setInWindow');
const copyFromWindow = require('copyFromWindow');

/**
 * Build ND aGTM shadow object
 * @lastupdate 12.02.2024 by Andi Petzoldt <andi@petzoldt.net>
 * @author Andi Petzoldt <andi@petzoldt.net>
 * @property {object} o
 * @param {object} c - config
 *   @param {object} c.debug - debug messages in browser console
 * @param {object} d - data object
 *   @param {object} d.d - a copy of the sandbox data object
 *   @param {object} d.e - the current event object
 *   @param {object} d.q - event queue
 * @param {object} f - function library
 */
var o = o || { c: {debug:false}, d:{ d:data, e:{}, q:[] }, f:{} };

// Get tag configuration
o.c.startevent = typeof data.startevent=='string' ? data.startevent : '';
o.c.submitevent = typeof data.submitevent=='string' ? data.submitevent : '';
o.c.maxclicks = typeof data.maxclicks=='string' ? data.maxclicks : 1; o.c.maxclicks = o.c.maxclicks * 1;
o.c.addparameter = typeof data.addparameter=='object' ? data.addparameter : [];
o.c.ua_event = typeof data.ua_event=='boolean' ? data.ua_event : false;

/**
 * Define function to send form submit info
 * @param {object} el - the object of the form
 * Usage: o.f.submit(el);
 */
o.f.submit = o.f.submit || function(el) {
  if (typeof el=='object' && typeof el.length!='number' && !el.eventModel) {
    // Prepare event
    o.c.addparameter.forEach(function(row) {
      o.d.e[row.pkey] = row.pvalue;
    });
    var e = JSON.parse(JSON.stringify(o.d.e));
    e.event = o.c.submitevent || 'form_submit';
    e.form_id = el.id || null;
    e.form_name = el.name || null;
    e.form_destination = el.action || null;
    e.form_length = el.elements || 0;
    e.class = el.class || null;
    e.target = el.target || null;
    e.parentID = el.parentID || null;
    e.parentClass = el.parentClass || null;
    e.html = el.html || null;
    e.text = el.text || '';
    if (o.c.ua_event) {
      e.event_category = 'User Experience';
      e.event_action = e.event;
      e.event_label = e.form_destination;
    }
    callInWindow('aGTM.f.fire', e);
    o.d.q.push(e);
  }
};

/**
 * Define function to send form (field) info, if a user has clicked into a form
 * @param {object} el - the object of the form field
 * Usage: o.f.formstart(el);
 */
o.f.formstart = o.f.formstart || function(el) {
  if (typeof el=='object' && typeof el.length!='number' && !el.eventModel) {
    var click_count = copyFromWindow('aGTMformClicks'); if (typeof click_count!='number') click_count = 0;
    click_count++;
    setInWindow('aGTMformClicks', click_count, true);
    if (click_count>o.c.maxclicks) return;
    // Prepare event
    o.c.addparameter.forEach(function(row) {
      o.d.e[row.pkey] = row.pvalue;
    });
    var e = JSON.parse(JSON.stringify(o.d.e));
    e.event = o.c.startevent || 'form_start';
    e.field_id = el.id || null;
    e.field_name = el.name || null;
    e.field_type = el.type || null;
    e.field_position = el.position || null;
    e.form_clicks = click_count;
    e.form = e.form || {};
    e.form_id = el.form.id || null;
    e.form_name = el.form.name || null;
    e.form_class = el.form.class || null;
    e.form_destination = el.form.action || null;
    e.form_length = el.form.elements || 0;
    e.target = el.target || null;
    e.parentID = el.parentID || null;
    e.parentClass = el.parentClass || null;
    e.html = el.html || null;
    e.text = el.text || '';
    if (o.c.ua_event) {
      e.event_category = 'User Experience';
      e.event_action = e.event;
      e.event_label = e.form_destination;
    }
    callInWindow('aGTM.f.fire', e);
    o.d.q.push(e);
  }
};

// Form Submit Listener
callInWindow('aGTM.f.addElLst','form','submit',function(e){o.f.submit(e);});
callInWindow('aGTM.f.observer','form','submit',function(e){o.f.submit(e);});

// Form Start Listener
callInWindow('aGTM.f.addElLst','form input, form textarea, form select','focus',function(e){o.f.formstart(e);});
callInWindow('aGTM.f.observer','form input, form textarea, form select','focus',function(e){o.f.formstart(e);});


// Call data.gtmOnSuccess when the tag is finished.
data.gtmOnSuccess();


