// Import needed libraries
const log = require('logToConsole');
const JSON = require('JSON');
const parseUrl = require('parseUrl');
const callInWindow = require('callInWindow');
const copyFromWindow = require('copyFromWindow');
const copyFromDataLayer = require('copyFromDataLayer');


/**
 * Build ND aGTM shadow object
 * @lastupdate 12.02.2024 by Andi Petzoldt <andi@petzoldt.net>
 * @author Andi Petzoldt <andi@petzoldt.net>
 * @property {object} o
 * @param {object} c - config
 *   @param {object} c.debug - debug messages in browser console
 * @param {object} d - data object
 *   @param {object} d.d - a copy of the sandbox data object
 *   @param {object} d.e - the current event object
 *   @param {object} d.q - event queue
 * @param {object} f - function library
 */
var o = o || { c: {debug:false}, d:{ d:data, init:false, e:null, q:[] }, f:{} };

// Get tag configuration
o.c.hostnamelist = typeof data.hostnamelist=='object' ? data.hostnamelist : [];
o.c.eventprefix = typeof data.eventprefix=='string' ? data.eventprefix : '';
o.c.addprefixtoall = typeof data.addprefixtoall=='boolean' ? data.addprefixtoall : false;
o.c.eventfilter = typeof data.eventfilter=='object' ? data.eventfilter : [];
o.c.dlparams = typeof data.dlparams=='object' ? data.dlparams : [];
o.c.addparameter = typeof data.addparameter=='object' ? data.addparameter : [];

// Check if page is in an iFrame
o.c.isiframe = callInWindow('aGTM.f.isIFrame');

// Prepare event
o.d.e = {};
if (o.c.eventname) o.d.e.event = o.c.eventname;
if (o.c.dlparams.length>0) {
  for (var i=0; i<o.c.dlparams.length; i++) {
    var dlp = o.c.dlparams[i].dlparam;
    var dlv = copyFromDataLayer(dlp);
    if (typeof dlv=='undefined') continue;
    o.d.e[dlp] = dlv;
  }
}
if (o.c.addparameter.length>0) {
  for (var j=0; j<o.c.addparameter.length; j++) {
    var row = o.c.addparameter[j];
    o.d.e[row.pkey] = row.pvalue;
  }
}

/**
 * Transform a URL to the hostname
 * @param {string} url - The URL
 * Usage: var hostname = o.f.url2host(url);
 */
o.f.url2host = o.f.url2host || function (url) {
  if (typeof url!='string') return '';
  if (!url || url.substring(0,4)!='http') return '';
  var obj = parseUrl(url);
  if (typeof obj!='object' || !obj || typeof obj.hostname!='string') return '';
  return obj.hostname;
};

/**
 * Message Listener Function
 * @param {object} msg - The Message Object what the iFrame has send
 * @param {string} org - The iFrame Origin URL
 * Usage: callInWindow('aGTM.f.evLstn', 'window', 'message', o.f.msgListener);
 */
o.f.msgListener = function (msg,org) {
  if (typeof msg!='object' || !msg) return;
  var ev = JSON.parse(JSON.stringify(msg));
  // Check hostname
  var hostname = org ? o.f.url2host(org) : '';
  if (o.c.hostnamelist.length>0 && hostname) {
    var track = false;
    for (var i=0; i<o.c.hostnamelist.length; i++) {
      var h = o.c.hostnamelist[i].hostname;
      if (o.c.hostnamelist[i].isregex) {
        if (callInWindow('aGTM.f.rTest', hostname, h)) { track = true; break; }
      } else if (h==hostname) { track = true; break; }
    }
    if (!track) return;
  }
  if (typeof ev.event=='string' && ev.event) {
    // Event Filter
    if (o.c.eventfilter.length>0) {
      // Include Filter
      var track = false; var count = 0;
      for (var i=0; i<o.c.eventfilter.length; i++) {
        var row = o.c.eventfilter[i];
        if (row.filtertype=='include') {
          count++;
          if (row.filterisregex) {
            if (callInWindow('aGTM.f.rTest', ev.event, row.filterpattern)) { track = true; break; }
          } else if (row.filterpattern==ev.event) { track = true; break; }
        }
      }
      if (!track && count>0) return;
      // Exclude Filter + Prefix
      for (var i=0; i<o.c.eventfilter.length; i++) {
        var row = o.c.eventfilter[i];
        if (row.filtertype=='exclude') {
          if (row.filterisregex) {
            if (callInWindow('aGTM.f.rTest', ev.event, row.filterpattern)) { return; }
          } else if (row.filterpattern==ev.event) { return; }
        }
      }
      // Prefix Filter
      if (!o.c.addprefixtoall) {
        for (var i=0; i<o.c.eventfilter.length; i++) {
          var row = o.c.eventfilter[i];
          if (row.filtertype=='prefix') {
            if (row.filterisregex) {
              if (callInWindow('aGTM.f.rTest', ev.event, row.filterpattern)) { ev.event = o.c.eventprefix + ev.event; }
            } else if (row.filterpattern==ev.event) { ev.event = o.c.eventprefix + ev.event; }
          }
        }
      }
    }
    // Set Event Name
    if (o.c.eventprefix) {
      if (o.c.addprefixtoall) ev.event = o.c.eventprefix + ev.event;
    }
  }
  // Merge predifined event with iFrame Message Event
  for (var key in ev) {
    o.d.e[key] = ev[key];
  }
  // Fire event
  callInWindow('aGTM.f.fire', o.d.e);
  o.d.q.push(o.d.e);
};

// iFrame/Top Listener
if (!o.c.isiframe) {
  callInWindow('aGTM.f.evLstn', 'window', 'message', o.f.msgListener);
  callInWindow('aGTM.f.ifHandshake');
}

// Call data.gtmOnSuccess when the tag is finished.
data.gtmOnSuccess();


