// Import needed libraries
const log = require('logToConsole');
const JSON = require('JSON');
const callInWindow = require('callInWindow');
const copyFromWindow = require('copyFromWindow');
const queryPermission = require('queryPermission');
const Math = require('Math');

/**
 * Build ND aGTM shadow object
 * @lastupdate 12.02.2024 by Andi Petzoldt <andi@petzoldt.net>
 * @author Andi Petzoldt <andi@petzoldt.net>
 * @property {object} o
 * @param {object} c - config
 *   @param {object} c.debug - debug messages in browser console
 * @param {object} d - data object
 *   @param {object} d.d - a copy of the sandbox data object
 *   @param {object} d.e - the current event object
 *   @param {object} d.q - event queue
 * @param {object} f - function library
 */
var o = o || { c: {debug:false}, d:{ d:data, e:{}, q:[] }, f:{} };

// Get tag configuration
o.c.eventname = data.eventname;
o.c.stepstring = typeof data.steps=='string' ? data.steps : '25,50,75,90';
o.c.addparameter = typeof data.addparameter=='object' ? data.addparameter : [];
o.c.noscrollevent = typeof data.noscrollevent=='boolean' ? data.noscrollevent : false;
o.c.isscrollevent = typeof data.isscrollevent=='boolean' ? data.isscrollevent : false;
o.c.ua_event = typeof data.ua_event=='boolean' ? data.ua_event : false;

// Initiate variables
o.d.steps = o.c.stepstring.split(',');
if (typeof o.d.scrolldepth_max!='number') o.d.scrolldepth_max = 0;
if (typeof o.d.scrolldepth_next!='number') o.d.scrolldepth_next = 0;
if (typeof o.d.viewport_width!='number') o.d.viewport_width = 0;
if (typeof o.d.viewport_height!='number') o.d.viewport_height = 0;
if (typeof o.d.viewport_relation!='number') o.d.viewport_relation = 1;
o.d.viewport_start = 1.5;
if (typeof o.d.document_width!='number') o.d.document_width = 0;
if (typeof o.d.document_height!='number') o.d.document_height = 0;
o.d.hasscrolled = false;
o.d.e.event = o.c.eventname;

/**
 * Define function to get the sizes of the document and the viewport
 * Usage: o.f.getSizes();
 */
o.f.getSizes = o.f.getSizes || function() {
  o.d.viewport_width=callInWindow('aGTM.f.getVal','w','innerWidth');
  o.d.viewport_height=callInWindow('aGTM.f.getVal','w','innerHeight');
  var offsetWidth=callInWindow('aGTM.f.getVal','b','offsetWidth');
  var offsetHeight=callInWindow('aGTM.f.getVal','b','offsetHeight');
  var clientWidth=callInWindow('aGTM.f.getVal','b','clientWidth');
  var clientHeight=callInWindow('aGTM.f.getVal','b','clientHeight');
  var scrollWidth=callInWindow('aGTM.f.getVal','b','scrollWidth');
  var scrollHeight=callInWindow('aGTM.f.getVal','b','scrollHeight');
  if(typeof offsetHeight=='number') {
    o.d.document_width = offsetWidth;
    o.d.document_height = offsetHeight;
  }
  if(typeof clientHeight=='number' && clientHeight>o.d.document_height) {
    o.d.document_width = clientWidth;
    o.d.document_height = clientHeight;
  }
  if(typeof scrollHeight=='number' && scrollHeight>o.d.document_height) {
    o.d.document_width = scrollWidth;
    o.d.document_height = scrollHeight;
  }
  if(o.d.viewport_height>0){
    o.d.viewport_relation = o.d.document_height / o.d.viewport_height;
  }
};

/**
 * Define function to get the current scroll depth
 * @returns {number} - the current scroll depth as Integer
 * Usage: o.f.getScrollDepth();
 */
o.f.getScrollDepth = o.f.getScrollDepth || function() {
  o.f.getSizes();
  var scrollPos = callInWindow('aGTM.f.getVal','w','scrollY') || callInWindow('aGTM.f.getVal','w','scrollTop') || callInWindow('aGTM.f.getVal','s','s') || 0;
  scrollPos+= o.d.viewport_height;
  if (o.d.document_height<=0 || scrollPos<=0) return 0;
  var scrollDepth = Math.round(100 / (o.d.document_height / scrollPos));
  //if (scrollDepth>scrolldepth_max) scrolldepth_max = scrollDepth;
  return scrollDepth;
};

/**
 * Define function to get the maximum scroll depth and send an event, if it has increased
 * Usage: o.f.getMaxScrollDepth();
 */
o.f.getMaxScrollDepth = o.f.getMaxScrollDepth || function() {
  if (o.d.viewport_relation>o.d.viewport_start && o.d.scrolldepth_next<o.d.steps.length) {
    // Prepare event
    o.c.addparameter.forEach(function(row) {
      o.d.e[row.pkey] = row.pvalue;
    });
    var scrolldepth = o.f.getScrollDepth();
    if (scrolldepth>o.d.scrolldepth_max) {
      o.d.scrolldepth_max = scrolldepth;
      if (o.c.isscrollevent && !o.d.hasscrolled) {
        o.d.hasscrolled = true;
        var ev = JSON.parse(JSON.stringify(o.d.e));
        ev.event = 'scrolled';
        ev.event_label = 'User has scrolled';
        if (o.c.ua_event) {
          ev.event_category = 'User Experience';
          ev.event_action = ev.event;
        }
        callInWindow('aGTM.f.fire', ev);
        o.d.q.push(ev);
      }
    }
    while (o.d.scrolldepth_max>=o.d.steps[o.d.scrolldepth_next]) {
      var e = JSON.parse(JSON.stringify(o.d.e));
      e.event_label = '>'+o.d.steps[o.d.scrolldepth_next].toString()+'%';
      e.scroll_depth = o.d.steps[o.d.scrolldepth_next];
      e.scrolldepth = o.d.steps[o.d.scrolldepth_next] * 1;
      e.viewport_relation = Math.round(o.d.viewport_relation);
      if (o.c.ua_event) {
        e.event_category = 'User Experience';
        e.event_action = e.event;
      }
      callInWindow('aGTM.f.fire', e);
      o.d.q.push(e);
      o.d.scrolldepth_next++;
    }
  }
};

// Run
o.f.getSizes();
var tempScrollDepth=o.f.getScrollDepth();
callInWindow('aGTM.f.evLstn','window','resize',o.f.getSizes);
if (o.d.viewport_relation>o.d.viewport_start) {
  callInWindow('aGTM.f.evLstn','window','scroll',o.f.getMaxScrollDepth);
} else if (o.c.noscrollevent) {
  // Prepare event
  o.c.addparameter.forEach(function(row) {
    o.d.e[row.pkey] = row.pvalue;
  });
  var e = JSON.parse(JSON.stringify(o.d.e));
  e.event_label = 'scroll tracking not necessary';
  e.viewport_relation = Math.round(o.d.viewport_relation);
  if (o.c.ua_event) {
    e.event_category = 'User Experience';
    e.event_action = e.event;
  }
  callInWindow('aGTM.f.fire', e);
  o.d.q.push(e);
}

// Call data.gtmOnSuccess when the tag is finished.
data.gtmOnSuccess();


