// Import needed libraries
const log = require('logToConsole');
const JSON = require('JSON');
const parseUrl = require('parseUrl');
const callInWindow = require('callInWindow');
const copyFromWindow = require('copyFromWindow');

/**
 * Build ND aGTM shadow object
 * @lastupdate 12.02.2024 by Andi Petzoldt <andi@petzoldt.net>
 * @author Andi Petzoldt <andi@petzoldt.net>
 * @property {object} o
 * @param {object} c - config
 *   @param {object} c.debug - debug messages in browser console
 * @param {object} d - data object
 *   @param {object} d.d - a copy of the sandbox data object
 *   @param {object} d.e - the current event object
 *   @param {object} d.q - event queue
 * @param {object} f - function library
 */
var o = o || { c: {debug:false}, d:{ d:data, e:{}, q:[] }, f:{} };

/**
 * Sets a configuration property to a specified value if the value matches the expected type.
 * Otherwise, it sets the property to a default value.
 * @param {Object} target - The object to which the property is added.
 * @param {Object} source - The object from which the value is sourced.
 * @param {string} key - The key in the source object to retrieve the value from.
 * @param {string} type - The expected JavaScript type of the source value.
 * @param {*} defaultValue - The default value to set if the source value does not match the expected type.
 */
function setConf(target, source, key, type, defaultValue) {
  target[key] = typeof source[key] === type ? source[key] : defaultValue;
}

// Get tag configuration
setConf(o.c, data, 'eventname', 'string', '');
setConf(o.c, data, 'selector', 'string', 'a');
setConf(o.c, data, 'downloadevent', 'string', '');
setConf(o.c, data, 'attach_outbound', 'boolean', false);
setConf(o.c, data, 'cross_domains', 'string', '');
if (o.c.cross_domains) o.c.cross_domains = callInWindow('aGTM.f.rReplace', o.c.cross_domains, '[^\\.0-9a-zA-Z_,-]', '');
setConf(o.c, data, 'cross_matching', 'string', 'domain');
setConf(o.c, data, 'usecontact', 'boolean', false);
setConf(o.c, data, 'contactprefix', 'string', '');
setConf(o.c, data, 'textfilter', 'object', []);
setConf(o.c, data, 'addparameter', 'object', []);
setConf(o.c, data, 'ua_event', 'boolean', false);

/**
 * Filters a text string by replacing specified patterns.
 * @param {string} t - The text to be filtered.
 * @returns {string} - The filtered text after all replacements have been made.
 */
o.f.textFilter = function (t) {
  if (typeof t!='string') return t;
  o.c.textfilter.forEach(function(row) {
    t = row.isregex ? callInWindow('aGTM.f.rReplace', t, row.pattern, row.replacetext) : t.replace(row.pattern, row.replacetext);
  });
  t = callInWindow('aGTM.f.rReplace', t, '[\\r\\n]', ' ');
  return t;
};

/**
 * Convert hostname to domain
 * @usage Send a hostname and get back the domain
 * @param {string} hostname - the hostname to convert
 * Usage: o.f.hostToDomain('sub.example.com');
 */
o.f.hostToDomain = o.f.hostToDomain || function(hostname) {
  if (typeof hostname!='string' || !hostname) hostname = callInWindow('aGTM.f.getVal','l','hostname');
  if (typeof hostname!='string') return '';
  var parts = hostname.split('.');
  if (parts.length <= 2) return hostname;
  return parts.slice(-2).join('.');
};

/**
 * Define function to send click info
 * @usage Gets click info and send an event
 * @param {object} el - the object of the clicked element
 * Usage: o.f.click();
 */
o.f.click = o.f.click || function(el) {
  if (typeof el!='object') return;
  // Prepare event
  o.c.addparameter.forEach(function(row) {
    o.d.e[row.pkey] = row.pvalue;
  });
  var ev = JSON.parse(JSON.stringify(o.d.e));
  ev.type = null;
  if (typeof el.href=='string' && el.href) {
    // Phone Check
    if (el.href.indexOf('tel:')===0 || callInWindow('aGTM.f.rTest', el.href, '^\\D{0,5}(\\+?\\d[\\d\\s\\-/().]{5,20}\\d)\\D{0,5}$')) ev.type = 'phone';
    // Mail Check
    if (!ev.type && (el.href.indexOf('mailto:')===0 || callInWindow('aGTM.f.rTest', el.href, '^\\W{0,5}([A-Z0-9._%+-]+(\\s*@\\s*|\\(\\s*[A-Z]{0,3}\\s*[DT]\\s*\\)\\s*|\\[\\s*[A-Z]{0,3}\\s*[DT]\\s*\\]\\s*|\\{\\s*[A-Z]{0,3}\\s*[DT]\\s*\\}\\s*)[A-Z0-9.-]+\\.[A-Z]{2,4})\\W{0,5}$'))) ev.type = 'email';
    // Download Check
    if (!ev.type) {
      var dlres = callInWindow('aGTM.f.rMatch', el.href, '\\/(([^\\/]+)(\\.(pdf|xlsx?|docx?|txt|rtf|csv|exe|key|pps|ppt|pptx|7z|pkg|rar|gz|zip|avi|mov|mp4|mpe?g|wmv|midi|mp3|wav|wma|jpe?g|png|gif|tiff|webp|svg|ai|ctx)))([\\?|#].*)?$');
      if (dlres) {
        ev.type = 'download';
        ev.file_name = dlres[1] || '';
        ev.file_extension = dlres[4] || '';
      }
    }
  }
  if (!ev.type && typeof el.text=='string' && el.text) {
    // Phone Check
    if (callInWindow('aGTM.f.rTest', el.text, '^\\D{0,5}(\\+?\\d[\\d\\s\\-/().]{5,20}\\d)\\D{0,5}$')) ev.type = 'phone';
    // Mail Check
    if (callInWindow('aGTM.f.rTest', el.text, '^\\W{0,5}([A-Z0-9._%+-]+(\\s*@\\s*|\\(\\s*[A-Z]{0,3}\\s*[DT]\\s*\\)\\s*|\\[\\s*[A-Z]{0,3}\\s*[DT]\\s*\\]\\s*|\\{\\s*[A-Z]{0,3}\\s*[DT]\\s*\\}\\s*)[A-Z0-9.-]+\\.[A-Z]{2,4})\\W{0,5}$')) ev.type = 'email';
  }
  ev.event = o.c.eventname || 'click';
  if (o.c.usecontact && ev.type && (ev.type=='email' || ev.type=='phone')) ev.event = o.c.contactprefix + ev.type;
  if (ev.type=='download' && o.c.downloadevent) ev.event = o.c.downloadevent;
  ev.action = 'click';
  ev.href = o.f.textFilter(el.href) || '';
  var urlobj = ev.href ? parseUrl(ev.href) : null;
  if (typeof urlobj!='object' || !urlobj) urlobj = {};
  ev.host = urlobj.hostname || '';
  ev.text = o.f.textFilter(el.text) || '';
  if (o.c.ua_event) {
    ev.event_category = 'User Experience';
    ev.event_action = ev.event;
    ev.event_label = ev.text;
  }
  ev.tagName = el.tagName || '';
  if (ev.tagName=='a') {
    if (!ev.host) ev.host = callInWindow('aGTM.f.getVal','l','hostname');
    var hostname = o.c.cross_matching=='domain' ? o.f.hostToDomain(ev.host) : ev.host;
    ev.outbound = 1;
    cross_domains.forEach(function(cd) {
      if (cd==hostname) ev.outbound = 0;
    });
    if (o.c.attach_outbound && ev.outbound) ev.event+= '_outbound';
    if (!ev.type) ev.type = 'link_' + ev.event;
  }
  if (!ev.type) {
    if (ev.tagName) {
      ev.type = ev.tagName+'_click';
    } else {
      ev.type = 'click';
    }
  }
  ev.target = el.target || '';
  ev.parentID = el.parentID || '';
  ev.parentClass = el.parentClass || '';
  ev.id = el.id || '';
  ev.name = el.name || '';
  ev.class = el.class || '';
  ev.src = el.src || '';
  ev.html = callInWindow('aGTM.f.rReplace', el.html, '[\\r\\n]', ' ') || null;
  callInWindow('aGTM.f.fire', ev);
  o.d.q.push(ev);
};

// Clean CrossDomain Array
var selfhost = callInWindow('aGTM.f.getVal','l','hostname') || '';
if (!o.c.cross_domains && typeof selfhost=='string') o.c.cross_domains = selfhost;
var cross_domains = o.c.cross_domains ? o.c.cross_domains.split(',') : [];
if (o.c.cross_matching=='domain') {
  var selfhost = o.f.hostToDomain(selfhost);
  for (var i=0; i<cross_domains.length; i++) {
    cross_domains[i] = o.f.hostToDomain(cross_domains[i]);
  }
}
if (selfhost && cross_domains.indexOf(selfhost) < 0) cross_domains.push(selfhost);

// Run
callInWindow('aGTM.f.addElLst',o.c.selector,'mousedown',function(e){o.f.click(e);});
callInWindow('aGTM.f.observer',o.c.selector,'mousedown',function(e){o.f.click(e);});

// Call data.gtmOnSuccess when the tag is finished.
data.gtmOnSuccess();


